import React, { useState } from "react";

export default function TextForm(props) {

  const HandleUppercaseConvert=()=>
  {
    console.log("Uppercase was clicked");
    setText("you have clicked on handleUpClick")
  }

  const HandleOnChange=()=>
  {
    console.log("On Change");
    setText()
  }

  const [text, setText] = useState('Enter text here');

  return (
    <div>
      <form>
        <h1>{props.heading}</h1>
        <div className="form-group">
          <label htmlFor="myBox">Text Area</label>
          <textarea className="form-control" id="myBox" rows="8" value={text}> onChange={HandleOnChange}</textarea>
          <small id="emailHelp" className="form-text text-muted">Enter text to analyze</small>
          <button className="btn btn-primary"onClick={HandleUppercaseConvert}>Convert to Uppercase</button>
        </div>
      </form>
    </div>
  );
}
